--  DDL for Procedure NAJLEPSI_NA_KAZDEJ_POZYCJI
--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "INF2S_KLOSJ"."NAJLEPSI_NA_KAZDEJ_POZYCJI" (
    p_result OUT SYS_REFCURSOR
)
AS
BEGIN
    OPEN p_result FOR
    SELECT
        position_in_game,
        nick_name,
        kda
    FROM (
        SELECT
            p.position_in_game,
            p.nick_name,
            ps.kda,
            RANK() OVER (PARTITION BY p.position_in_game ORDER BY ps.kda DESC) AS rnk
        FROM
            players p
            JOIN players_statistics ps ON p.id_player = ps.player_id
    )
    WHERE rnk = 1
    ORDER BY position_in_game;
END;

