--  DDL for Function SPRAWDZ_WIEK_PLAYERA
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE FUNCTION "INF2S_KLOSJ"."SPRAWDZ_WIEK_PLAYERA" (p_id_player IN NUMBER)
RETURN NUMBER
IS
    v_age NUMBER;
BEGIN
    SELECT age INTO v_age FROM players WHERE id_player = p_id_player;

    IF v_age IS NULL THEN
        RETURN 0;
    ELSIF v_age < 15 THEN
        RETURN 0;
    ELSIF v_age > 45 THEN
        RETURN 0;
    ELSE
        RETURN 1;
    END IF;

EXCEPTION
    WHEN NO_DATA_FOUND THEN
        RETURN 0;
    WHEN TOO_MANY_ROWS THEN
        RETURN 0;
    WHEN OTHERS THEN
        RETURN 0;
END;

