--------------------------------------------------------
--  DDL for Function SREDNIE_STATYSTYKI_ZAWODNIKA
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE FUNCTION "INF2S_KLOSJ"."SREDNIE_STATYSTYKI_ZAWODNIKA" (p_id_player IN NUMBER)
RETURN SYS_REFCURSOR
AS
    rc SYS_REFCURSOR;
BEGIN
    OPEN rc FOR
        SELECT
            NVL(AVG(kills), 0) AS avg_kills,
            NVL(AVG(assist), 0) AS avg_assist,
            NVL(AVG(death), 0) AS avg_death,
            NVL(AVG(kda), 0) AS avg_kda
        FROM players_statistics
        WHERE player_id = p_id_player;
    RETURN rc;
END;

